// n8n HTTP Request Response: statusCode ist direkt in json verf√ºgbar
// Die Items kommen von der Merge Node, die alle parallelen HTTP Requests kombiniert
const allItems = $input.all();

// Direkt Status Codes extrahieren (n8n speichert statusCode in json)
const test1 = allItems[0] && allItems[0].json && allItems[0].json.statusCode ? allItems[0].json.statusCode : null;
const test2 = allItems[1] && allItems[1].json && allItems[1].json.statusCode ? allItems[1].json.statusCode : null;
const test3 = allItems[2] && allItems[2].json && allItems[2].json.statusCode ? allItems[2].json.statusCode : null;
const test4 = allItems[3] && allItems[3].json && allItems[3].json.statusCode ? allItems[3].json.statusCode : null;
const test5 = allItems[4] && allItems[4].json && allItems[4].json.statusCode ? allItems[4].json.statusCode : null;
const test6 = allItems[5] && allItems[5].json && allItems[5].json.statusCode ? allItems[5].json.statusCode : null;
const test7 = allItems[6] && allItems[6].json && allItems[6].json.statusCode ? allItems[6].json.statusCode : null;
const test8 = allItems[7] && allItems[7].json && allItems[7].json.statusCode ? allItems[7].json.statusCode : null;
const test9 = allItems[8] && allItems[8].json && allItems[8].json.statusCode ? allItems[8].json.statusCode : null;
const test10 = allItems[9] && allItems[9].json && allItems[9].json.statusCode ? allItems[9].json.statusCode : null;

// Count successes
const allTests = [test1, test2, test3, test4, test5, test6, test7, test8, test9, test10];
const successCount = allTests.filter(function(t) { return t === 200; }).length;
const totalCount = allTests.length;
const successRate = Math.round((successCount / totalCount) * 100);

// Determine overall status
const allPassed = allTests.every(function(t) { return t === 200; });

// Create detailed result
const result = {
  timestamp: new Date().toISOString(),
  testSuite: 'Comprehensive Health Checks',
  overallStatus: allPassed ? '‚úÖ ALL TESTS PASSED' : '‚ùå SOME TESTS FAILED',
  successRate: successRate + '% (' + successCount + '/' + totalCount + ')',
  healthChecks: {
    homepage: test1 === 200 ? '‚úÖ 200' : '‚ùå ' + (test1 || 'FAILED'),
    annahme: test2 === 200 ? '‚úÖ 200' : '‚ùå ' + (test2 || 'FAILED'),
    liste: test3 === 200 ? '‚úÖ 200' : '‚ùå ' + (test3 || 'FAILED'),
    kanban: test4 === 200 ? '‚úÖ 200' : '‚ùå ' + (test4 || 'FAILED'),
    partner: test5 === 200 ? '‚úÖ 200' : '‚ùå ' + (test5 || 'FAILED')
  },
  assetChecks: {
    firebaseConfig: test6 === 200 ? '‚úÖ 200' : '‚ùå ' + (test6 || 'FAILED'),
    firebaseSDK: test7 === 200 ? '‚úÖ 200' : '‚ùå ' + (test7 || 'FAILED'),
    errorHandler: test8 === 200 ? '‚úÖ 200' : '‚ùå ' + (test8 || 'FAILED'),
    pdfGenerator: test9 === 200 ? '‚úÖ 200' : '‚ùå ' + (test9 || 'FAILED'),
    signaturePad: test10 === 200 ? '‚úÖ 200' : '‚ùå ' + (test10 || 'FAILED')
  },
  rawResults: {
    test1_homepage: test1,
    test2_annahme: test2,
    test3_liste: test3,
    test4_kanban: test4,
    test5_partner: test5,
    test6_firebaseConfig: test6,
    test7_firebaseSDK: test7,
    test8_errorHandler: test8,
    test9_pdfGenerator: test9,
    test10_signaturePad: test10
  },
  failedTests: []
};

// Build failedTests array manually (no chaining)
for (var i = 0; i < allTests.length; i++) {
  if (allTests[i] !== 200) {
    result.failedTests.push('Test ' + (i + 1) + ': Status ' + (allTests[i] || 'FAILED'));
  }
}

return [{ json: result }];

