// Create comprehensive test log
const timestamp = new Date().toISOString();
const date = new Date().toLocaleDateString('de-DE');
const time = new Date().toLocaleTimeString('de-DE');

const inputData = $input.first().json;

// Create formatted log entry
const logEntry = {
  // Metadata
  timestamp: timestamp,
  date: date,
  time: time,
  testSuite: 'Comprehensive Health Checks',

  // Summary
  overallStatus: inputData.overallStatus,
  successRate: inputData.successRate,
  emailSubject: inputData.emailSubject,
  status: inputData.status,

  // Individual Test Results (for spreadsheet)
  test1_homepage: inputData.rawResults?.test1_homepage || 'N/A',
  test2_annahme: inputData.rawResults?.test2_annahme || 'N/A',
  test3_liste: inputData.rawResults?.test3_liste || 'N/A',
  test4_kanban: inputData.rawResults?.test4_kanban || 'N/A',
  test5_partner: inputData.rawResults?.test5_partner || 'N/A',
  test6_firebaseConfig: inputData.rawResults?.test6_firebaseConfig || 'N/A',
  test7_firebaseSDK: inputData.rawResults?.test7_firebaseSDK || 'N/A',
  test8_errorHandler: inputData.rawResults?.test8_errorHandler || 'N/A',
  test9_pdfGenerator: inputData.rawResults?.test9_pdfGenerator || 'N/A',
  test10_signaturePad: inputData.rawResults?.test10_signaturePad || 'N/A',

  // Human-readable format for console
  summary: `
üìä TEST SUITE RESULTS
=====================
Timestamp: ${date} ${time}
Status: ${inputData.overallStatus}
Success Rate: ${inputData.successRate}

‚úÖ HEALTH CHECKS:
  - Homepage: ${inputData.healthChecks?.homepage || 'N/A'}
  - Annahme: ${inputData.healthChecks?.annahme || 'N/A'}
  - Liste: ${inputData.healthChecks?.liste || 'N/A'}
  - Kanban: ${inputData.healthChecks?.kanban || 'N/A'}
  - Partner: ${inputData.healthChecks?.partner || 'N/A'}

üì¶ ASSET CHECKS:
  - Firebase Config: ${inputData.assetChecks?.firebaseConfig || 'N/A'}
  - Firebase SDK: ${inputData.assetChecks?.firebaseSDK || 'N/A'}
  - Error Handler: ${inputData.assetChecks?.errorHandler || 'N/A'}
  - PDF Generator: ${inputData.assetChecks?.pdfGenerator || 'N/A'}
  - Signature Pad: ${inputData.assetChecks?.signaturePad || 'N/A'}
${inputData.failedTests?.length > 0 ? `
‚ùå FAILED TESTS:
${inputData.failedTests.map(t => '  - ' + t).join('\n')}` : ''}
`
};

console.log(logEntry.summary);

return [{ json: logEntry }];
