const responseData = $input.first().json;
const html = typeof responseData === 'string' ? responseData : (responseData.body || '');

// Check if Firebase SDK loaded
const hasFirebaseApp = html.includes('firebase-app');
const hasFirestore = html.includes('firebase-firestore') || html.includes('firestore');
const hasStorage = html.includes('firebase-storage') || html.includes('storage');
const hasFirebaseConfig = html.includes('firebase-config.js');

// Check DOM elements
const hasKennzeichenInput = html.includes('id="kennzeichen"') || html.includes('kennzeichen');
const hasKundennameInput = html.includes('id="kundenname"') || html.includes('kundenname');
const hasSaveButton = html.includes('Speichern');
const hasPhotoInput = html.includes('photoInput') || html.includes('photo');
const hasSignaturePad = html.includes('signaturePad') || html.includes('signature');

// Check utility scripts
const hasErrorHandler = html.includes('error-handler.js');
const hasPDFGenerator = html.includes('pdf-generator.js');

const allChecks = [
  hasFirebaseApp, hasFirestore, hasStorage, hasFirebaseConfig,
  hasKennzeichenInput, hasKundennameInput, hasSaveButton,
  hasPhotoInput, hasSignaturePad, hasErrorHandler, hasPDFGenerator
];

const totalChecks = allChecks.length;
const passedChecks = allChecks.filter(function(c) { return c === true; }).length;
const passed = passedChecks === totalChecks;

const result = {
  timestamp: new Date().toISOString(),
  testName: 'Firebase Configuration Check',
  firebaseSDK: {
    firebaseApp: hasFirebaseApp ? '‚úÖ Loaded' : '‚ùå Missing',
    firestore: hasFirestore ? '‚úÖ Loaded' : '‚ùå Missing',
    storage: hasStorage ? '‚úÖ Loaded' : '‚ùå Missing',
    config: hasFirebaseConfig ? '‚úÖ Loaded' : '‚ùå Missing'
  },
  domElements: {
    kennzeichenInput: hasKennzeichenInput ? '‚úÖ Present' : '‚ùå Missing',
    kundennameInput: hasKundennameInput ? '‚úÖ Present' : '‚ùå Missing',
    saveButton: hasSaveButton ? '‚úÖ Present' : '‚ùå Missing',
    photoInput: hasPhotoInput ? '‚úÖ Present' : '‚ùå Missing',
    signaturePad: hasSignaturePad ? '‚úÖ Present' : '‚ùå Missing'
  },
  utilityScripts: {
    errorHandler: hasErrorHandler ? '‚úÖ Loaded' : '‚ùå Missing',
    pdfGenerator: hasPDFGenerator ? '‚úÖ Loaded' : '‚ùå Missing'
  },
  passed: passed,
  passRate: passedChecks + '/' + totalChecks + ' (' + Math.round((passedChecks/totalChecks)*100) + '%)',
  status: passed ? '‚úÖ ALL CHECKS PASSED' : '‚ùå SOME CHECKS FAILED'
};

return [{ json: result }];

