// Parse HTML to check if Firebase is properly configured
const html = $input.first().json.body;

// Check 1: Firebase SDK script tags present
const hasFirebaseApp = html.includes('firebase-app');
const hasFirestore = html.includes('firebase-firestore');
const hasStorage = html.includes('firebase-storage');

// Check 2: firebase-config.js loaded
const hasFirebaseConfig = html.includes('firebase-config.js');

// Check 3: Critical DOM elements present
const hasKennzeichenInput = html.includes('id="kennzeichen"');
const hasKundennameInput = html.includes('id="kundenname"');
const hasSaveButton = html.includes('Speichern');
const hasPhotoInput = html.includes('photoInput');
const hasSignaturePad = html.includes('signaturePad');

// Check 4: Error handler loaded
const hasErrorHandler = html.includes('error-handler.js');

// Check 5: PDF generator loaded
const hasPDFGenerator = html.includes('pdf-generator.js');

const result = {
  timestamp: new Date().toISOString(),
  testName: 'Firebase Configuration Check',

  // Firebase SDK Checks
  firebaseSDK: {
    firebaseApp: hasFirebaseApp ? '‚úÖ Loaded' : '‚ùå Missing',
    firestore: hasFirestore ? '‚úÖ Loaded' : '‚ùå Missing',
    storage: hasStorage ? '‚úÖ Loaded' : '‚ùå Missing',
    config: hasFirebaseConfig ? '‚úÖ Loaded' : '‚ùå Missing'
  },

  // DOM Elements Check
  domElements: {
    kennzeichenInput: hasKennzeichenInput ? '‚úÖ Present' : '‚ùå Missing',
    kundennameInput: hasKundennameInput ? '‚úÖ Present' : '‚ùå Missing',
    saveButton: hasSaveButton ? '‚úÖ Present' : '‚ùå Missing',
    photoInput: hasPhotoInput ? '‚úÖ Present' : '‚ùå Missing',
    signaturePad: hasSignaturePad ? '‚úÖ Present' : '‚ùå Missing'
  },

  // Utility Scripts
  utilityScripts: {
    errorHandler: hasErrorHandler ? '‚úÖ Loaded' : '‚ùå Missing',
    pdfGenerator: hasPDFGenerator ? '‚úÖ Loaded' : '‚ùå Missing'
  },

  // Overall Status
  allChecks: [
    hasFirebaseApp, hasFirestore, hasStorage, hasFirebaseConfig,
    hasKennzeichenInput, hasKundennameInput, hasSaveButton,
    hasPhotoInput, hasSignaturePad, hasErrorHandler, hasPDFGenerator
  ],

  passed: null // Will be calculated below
};

// Calculate pass/fail
const totalChecks = result.allChecks.length;
const passedChecks = result.allChecks.filter(c => c === true).length;
result.passed = passedChecks === totalChecks;
result.passRate = `${passedChecks}/${totalChecks} (${Math.round((passedChecks/totalChecks)*100)}%)`;
result.status = result.passed ? '‚úÖ ALL CHECKS PASSED' : '‚ùå SOME CHECKS FAILED';

return [{ json: result }];

